mapper.update:
update prescription_info
set prescriptionId = #{prescriptionId}, prescriptionName = #{prescriptionName}, prescriptionCompose = #{prescriptionCompose}, prescriptionProvenance = #{prescriptionProvenance},
prescriptionAttend = #{prescriptionAttend}, prescriptionUsageDosage = #{prescriptionUsageDosage}, prescriptionPreparationFunction = #{prescriptionPreparationFunction},
compose = #{compose}
where prescriptionInfoId = #{prescriptionInfoId}
